import React,{useState} from 'react';
import { View, Text, Image,StyleSheet,KeyboardAvoidingView } from 'react-native';
import { TextInput, Button } from 'react-native-paper';

const LoginScreen = () => {
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  return (
    <KeyboardAvoidingView behavior='position' style={styles.box} >
    <View style={styles.box1}>
      <Image style={{height:200,width:200}} source={require('../assets/cnqlogo.png')}/>
      <Text style={styles.text}>Please login to continue!</Text>
    </View>
    <View style={styles.box2}>
      <TextInput
      style={styles.botton} 
      label="Email"
      value={email}
      mode='outlined'
      onChangeText={text=>setEmail(text)}
      />
      <TextInput
      style={styles.botton} 
      label="Password"
      value={password}
      mode='outlined'
      secureTextEntry={true}
      onChangeText={text=>setPassword(text)}
      />
       <Button mode="contained"  style={styles.botton} onPress={() => console.log('Pressed')}>
     Login
       </Button>
     </View>
    </KeyboardAvoidingView>
  )
}

const styles = StyleSheet.create({
  box:{
    alignItems:'center',
  },
  box1:{
    display: 'flex',
    justifyContent:'center',
    alignItems:'center',
  },
  box2:{
    display: 'flex',
    paddingTop:50,
    width: 350,
    justifyContent:'space-evenly',
    height: "55%",
  },
  text:{
    fontSize:22,
  },
  botton:{
    height: 50,
    marginTop:12,
    justifyContent:"center"
  }
});

export default LoginScreen;
