import React,{useEffect,useState} from 'react'
import { View, Text ,FlatList,StyleSheet,Linking,Platform} from 'react-native'
import { Avatar, Button, Card, Title, Paragraph } from 'react-native-paper';
// import firestore from '@react-native-firebase/firestore';

const ListItemScreen = () => {

    const item=[
        {name:'iPhone',
        year:'2022',
         phone:121012,
         image:'https://images.unsplash.com/photo-1603791239531-1dda55e194a6?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8YXBwbGUlMjBpcGhvbmV8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60',
         desc:'I want to sell this phone because this i gave to my and she had not accepted this.'
        },
        {name:'iPhone',
        year:'2022',
         phone:121012,
         image:'https://images.unsplash.com/photo-1603791239531-1dda55e194a6?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8YXBwbGUlMjBpcGhvbmV8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60',
         desc:'I want to sell this phone because this i gave to my and she had not accepted this.'
        },
        {name:'iPhone',
        year:'2022',
         phone:121012,
         image:'https://images.unsplash.com/photo-1603791239531-1dda55e194a6?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8YXBwbGUlMjBpcGhvbmV8ZW58MHx8MHx8&auto=format&fit=crop&w=500&q=60',
         desc:'I want to sell this phone because this i gave to my and she had not accepted this.'
        },
    ]

    const renderItem = ()=>{
      return(
          <Card style={styles.card}>
        <Card.Title title={item.name}  />
        <Card.Content>
          <Paragraph>{item.desc}</Paragraph>
          <Paragraph>{item.year}</Paragraph>
        </Card.Content>
        <Card.Cover source={{ uri: item.image }} />
        <Card.Actions>
          <Button>{item.price}</Button>
          <Button onPress={()=>openDial()}>call seller</Button>
        </Card.Actions>
      </Card>  
      )
    }
    return (
        <View>
            <FlatList 
            data={item.reverse()}
            keyExtractor={(item)=>item.phone}
            renderItem={({item})=>renderItem(item)}
            />
        </View>
    )
}


const styles = StyleSheet.create({
    card:{
        margin:10,
        elevation:2
    }
     });
    
    

export default ListItemScreen
